import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Validation from './signupValidation';
import axios from 'axios';

function Signup() {
  const [values, setValues] = useState({
    full_name: '',
    mobile_no: '',
    email: '',
    password: ''
  });
  const navigate = useNavigate();
  const [errors, setErrors] = useState({});
  const [showPassword, setShowPassword] = useState(false);
  const [otpSent, setOtpSent] = useState(false);
  const [otp, setOtp] = useState('');
  const [otpError, setOtpError] = useState('');

  const handleInput = (event) => {
    const { name, value } = event.target;
    setValues((prev) => ({ ...prev, [name]: value }));
  };

  // Request OTP for mobile number
  const handleOtpRequest = () => {
    if (values.mobile_no) {
      axios.post('http://localhost:4000/signup', { mobile: values.mobile_no })
        .then(res => {
          if (res.data.otpSent) {
            setOtpSent(true);
            setOtpError('');
          } else {
            setOtpError('Failed to send OTP. Try again.');
          }
        })
        .catch(err => console.error(err));
    } else {
      setOtpError('Please enter a valid mobile number');
    }
  };

  // Handle OTP input
  const handleOtpChange = (event) => {
    setOtp(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setErrors(Validation(values));
    
    if (!errors.full_name && !errors.mobile_no && !errors.email && !errors.password) {
      // First, validate the OTP
      axios.post('http://localhost:4000/verify-otp', { mobile: values.mobile_no, otp })
        .then(res => {
          if (res.data.verified) {
            // Proceed with signup if OTP is verified
            axios.post('http://localhost:4000/complete-signup', values)
              .then(res => {
                console.log(res);
                navigate('/');
              })
              .catch(err => console.error(err));
          } else {
            setOtpError('Invalid OTP');
          }
        })
        .catch(err => console.error(err));
    }
  };

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-6 col-lg-5">
          <div className="card shadow-sm p-4">
            <h4 className="text-center mb-4">Sign Up</h4>
            <form onSubmit={handleSubmit}>
              <div className="mb-3">
                <label htmlFor="full_name" className="form-label">Full Name</label>
                <input
                  type="text"
                  className="form-control"
                  name="full_name"
                  placeholder="Enter full name"
                  onChange={handleInput}
                />
                {errors.full_name && <span className="text-danger">{errors.full_name}</span>}
              </div>
              <div className="mb-3">
                <label htmlFor="mobile_no" className="form-label">Mobile No</label>
                <input
                  type="text"
                  className="form-control"
                  name="mobile_no"
                  maxLength="10"
                  placeholder="Enter mobile no"
                  onChange={handleInput}
                />
                {errors.mobile_no && <span className="text-danger">{errors.mobile_no}</span>}
                <button type="button" className="btn btn-secondary mt-2" onClick={handleOtpRequest}>
                  Request OTP
                </button>
                {otpError && <span className="text-danger">{otpError}</span>}
              </div>
              {otpSent && (
                <div className="mb-3">
                  <label htmlFor="otp" className="form-label">Enter OTP</label>
                  <input
                    type="text"
                    className="form-control"
                    name="otp"
                    maxLength="6"
                    placeholder="Enter OTP"
                    value={otp}
                    onChange={handleOtpChange}
                  />
                </div>
              )}
              <div className="mb-3">
                <label htmlFor="email" className="form-label">Email address</label>
                <input
                  type="email"
                  className="form-control"
                  name="email"
                  placeholder="Enter email"
                  onChange={handleInput}
                />
                {errors.email && <span className="text-danger">{errors.email}</span>}
              </div>
              <div className="mb-3">
                <label htmlFor="password" className="form-label">Password</label>
                <div className="d-flex">
                  <input
                    type={showPassword ? "text" : "password"}
                    className="form-control w-75"
                    name="password"
                    placeholder="Password"
                    onChange={handleInput}
                  />
                  <button
                    type="button"
                    className="btn btn-default border btn-sm w-25"
                    onClick={() => setShowPassword(!showPassword)}
                  >
                    {showPassword ? "Hide" : "Show"}
                  </button>
                </div>
                {errors.password && <span className="text-danger">{errors.password}</span>}
              </div>
              <div className="d-grid gap-2">
                <button type="submit" className="btn btn-primary">Sign Up</button>
                <Link to="/" className="btn btn-outline-secondary">Login</Link>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Signup;
