import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Signup() {
  const [values, setValues] = useState({
    full_name: '',
    mobile_no: '',
    email: '',
    password: '',
    otp: ''
  });
  const [otpSent, setOtpSent] = useState(false);
  const [otpError, setOtpError] = useState('');
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const handleInput = (event) => {
    const { name, value } = event.target;
    setValues((prev) => ({ ...prev, [name]: value }));
  };

  const handleOtpRequest = () => {
    if (values.mobile_no) {
      // Request OTP from backend
      axios.post('http://localhost:4000/request-otp', { mobile_no: values.mobile_no })
        .then(res => {
          if (res.data.success) {
            setOtpSent(true);
            setOtpError('');
          } else {
            setOtpError('Failed to send OTP. Try again.');
          }
        })
        .catch(err => console.error(err));
    } else {
      setOtpError('Please enter a valid mobile number');
    }
  };

  const handleOtpChange = (event) => {
    setValues((prev) => ({ ...prev, otp: event.target.value }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    if (values.otp) {
      // Verify OTP with the backend
      axios.post('http://localhost:4000/verify-otp', { mobile_no: values.mobile_no, otp: values.otp })
        .then(res => {
          if (res.data.success) {
            // Proceed with signup after OTP is verified
            const { full_name, mobile_no, email, password } = values;
            axios.post('http://localhost:4000/signup', { full_name, mobile_no, email, password })
              .then(res => {
                if (res.data.success) {
                  navigate('/');
                } else {
                  setOtpError('Signup failed, please try again.');
                }
              })
              .catch(err => {
                console.error(err);
                setOtpError('Signup failed, please try again.');
              });
          } else {
            setOtpError('Invalid OTP');
          }
        })
        .catch(err => {
          console.error(err);
          setOtpError('Error verifying OTP');
        });
    } else {
      setOtpError('Please enter the OTP');
    }
  };

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-6 col-lg-5">
          <div className="card shadow-sm p-4">
            <h4 className="text-center mb-4">Sign Up</h4>
            <form onSubmit={handleSubmit}>
              <div className="mb-3">
                <label htmlFor="full_name" className="form-label">Full Name</label>
                <input
                  type="text"
                  className="form-control"
                  name="full_name"
                  placeholder="Enter full name"
                  onChange={handleInput}
                />
              </div>
              <div className="mb-3">
                <label htmlFor="mobile_no" className="form-label">Mobile No</label>
                <input
                  type="text"
                  className="form-control"
                  name="mobile_no"
                  maxLength="10"
                  placeholder="Enter mobile no"
                  onChange={handleInput}
                />
                <button type="button" className="btn btn-secondary mt-2" onClick={handleOtpRequest}>
                  Request OTP
                </button>
              </div>
              {otpSent && (
                <div className="mb-3">
                  <label htmlFor="otp" className="form-label">Enter OTP</label>
                  <input
                    type="text"
                    className="form-control"
                    name="otp"
                    maxLength="6"
                    placeholder="Enter OTP"
                    value={values.otp}
                    onChange={handleOtpChange}
                  />
                </div>
              )}
              {otpError && <span className="text-danger">{otpError}</span>}
              <div className="mb-3">
                <label htmlFor="email" className="form-label">Email address</label>
                <input
                  type="email"
                  className="form-control"
                  name="email"
                  placeholder="Enter email"
                  onChange={handleInput}
                />
              </div>
              <div className="mb-3">
                <label htmlFor="password" className="form-label">Password</label>
                <input
                  type="password"
                  className="form-control"
                  name="password"
                  placeholder="Password"
                  onChange={handleInput}
                />
              </div>
              <button type="submit" className="btn btn-primary w-100">
                Sign Up
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Signup;
